mapper.select:
SELECT R.REPLY_ID AS replyId, R.ROOT_REPLY_ID AS rootReplyId, R.USER_CODE AS userCode, R.POST_ID AS postId, R.REPLY_CONTENT AS replyContent, TO_CHAR(R.CREATED_DATE, 'YYYY-MM-DD HH24:MI:SS') AS createdDate, NL.NICKNAME AS nickname
FROM ( SELECT * FROM REPLY WHERE USER_CODE = #{userCode, jdbcType=INTEGER} ORDER BY CREATED_DATE DESC ) R LEFT JOIN ( SELECT USER_CODE, NICKNAME FROM ( SELECT USER_CODE, NICKNAME, ROW_NUMBER() OVER (PARTITION BY USER_CODE ORDER BY LAST_UPDATED_DATE DESC) AS RN FROM NICKNAME_LOG ) WHERE RN = 1 ) NL ON R.USER_CODE = NL.USER_CODE
WHERE ROWNUM <= #{limit, jdbcType=INTEGER}
