mapper.insert:
INSERT INTO NICKNAME_LOG (
    NICKNAME_LOG_ID, USER_CODE, NICKNAME, LAST_UPDATED_DATE
)
VALUES (
           NICKNAME_LOG_SEQ.NEXTVAL, #{userCode}, #{nickname}, SYSDATE
       )

mapper.select:
SELECT COUNT(*)
FROM USERS
WHERE USER_ID = #{userId}
  AND USER_NAME = #{userName}
  AND USER_TEL = #{userTel}
